(21, 0, 'So Placeholder', 'ocmod-soplaceholder', 'OpencartWorks', '1.0.0', 'http://www.OpencartWorks.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?> \r\n<modification> \r\n <name><![CDATA[So Placeholder]]></name> \r\n <code>ocmod-soplaceholder</code> \r\n <version><![CDATA[1.0.0]]></version> \r\n <author><![CDATA[OpencartWorks]]></author> \r\n <link>http://www.OpencartWorks.com</link> \r\n \r\n<!--========Catalog/controller/product=============--> \r\n <file path=\"catalog/controller/product/category.php\"> \r\n  <operation> \r\n	 <search><![CDATA[$image = $this->model_tool_image->resize(\'placeholder.png\', $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_height\'));]]></search> \r\n	 <add position=\"replace\"><![CDATA[$image = $this->model_tool_image->resize($placeholder, $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_height\'));]]></add> \r\n </operation>\r\n <operation > \r\n	 <search ><![CDATA[// Set the last category breadcrumb]]></search> \r\n	 <add position=\"before\"><![CDATA[ \r\n		$placeholder=\'placeholder.png\';\r\n		if($this->soconfig->get_settings(\'placeholder_status\')){\r\n			$placeholder = $this->soconfig->get_settings(\'placeholder_img\');\r\n		}else{\r\n			$placeholder = \'placeholder.png\';\r\n		}\r\n	 ]]> \r\n	 </add> \r\n </operation> \r\n  <operation > \r\n	 <search ><![CDATA[$data[\'thumb\'] = \'\';]]></search> \r\n	 <add position=\"replace\"><![CDATA[ \r\n		$data[\'thumb\'] = $this->model_tool_image->resize($placeholder, $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_category_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_category_height\'));\r\n	 ]]> \r\n	 </add> \r\n </operation>\r\n <operation > \r\n	 <search ><![CDATA[$data[\'categories\'][] ]]></search> \r\n	 <add position=\"before\"><![CDATA[ \r\n	 if ($result[\'image\']) $image = $this->model_tool_image->resize($result[\'image\'], $this->config->get(\'theme_\' .$this->config->get(\'config_theme\') . \'_image_category_width\'), $this->config->get(\'theme_\' .$this->config->get(\'config_theme\') . \'_image_category_height\')); \r\n	 else $image = $this->model_tool_image->resize($placeholder, $this->config->get(\'theme_\' .$this->config->get(\'config_theme\') . \'_image_category_width\'),$this->config->get( \'theme_\' .$this->config->get(\'config_theme\') . \'_image_category_height\')); \r\n	 ]]> \r\n	 </add> \r\n </operation> \r\n \r\n <operation> \r\n	 <search ><![CDATA[$data[\'categories\'][]]]></search> \r\n	 <add position=\"after\"><![CDATA[ \'thumb\' => $image,]]></add> \r\n </operation> \r\n \r\n\r\n \r\n </file> \r\n \r\n\r\n</modification> ', 1, '2018-01-04 09:04:09', '2018-03-22 15:32:49'),
(3, 0, 'So ThemeConfig', 'ocmod-soconfig', 'OpenCartWorks', '3.0.0', 'http://www.OpencartWorks.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<modification>\r\n	<name><![CDATA[So ThemeConfig]]></name>\r\n	<code>ocmod-soconfig</code>\r\n    <version><![CDATA[3.0.0]]></version>\r\n    <author><![CDATA[OpenCartWorks]]></author>\r\n	<link>http://www.OpencartWorks.com</link>\r\n	\r\n<!--========Catalog/controller/common=============-->\r\n    <file path=\"catalog/controller/common/language.php\">\r\n		<operation>\r\n			<search ><![CDATA[$data[\'languages\'] = array();]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n			if (isset($this->request->get[\'lang\']) && $this->request->get[\'lang\'] == \'ar-ar\') {\r\n				$this->session->data[\'language\'] = $this->request->get[\'lang\'];\r\n				$this->response->redirect($this->url->link(\'common/home\'));\r\n			}\r\n		]]></add>\r\n		</operation>\r\n	</file>\r\n	\r\n	<file path=\"catalog/controller/common/cart.php\">\r\n		<operation >\r\n			<search ><![CDATA[public function index()]]></search>\r\n			<add position=\"after\" offset=\"2\"><![CDATA[\r\n		/*======Show Themeconfig=======*/\r\n		$this->load->language(\'extension/soconfig/cart\');\r\n		$data[\'objlang\'] = $this->language;\r\n		]]></add>\r\n		</operation>\r\n	</file>\r\n	\r\n	<file path=\"catalog/controller/common/header.php\">\r\n		<operation >\r\n			<search ><![CDATA[public function index()]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n		/*======Show Themeconfig=======*/\r\n		 $data[\'soconfig\'] = $this->soconfig; \r\n		 $this->load->language(\'extension/soconfig/compare\'); \r\n		 $data[\'objlang\'] = $this->language; \r\n		 $data[\'lang_id\'] = $this->config->get(\'config_language_id\'); \r\n		 $data[\'theme_directory\'] = $this->config->get(\'theme_default_directory\'); \r\n		 $data[\'url_layoutbox\'] = isset($this->request->get[\'layoutbox\']) ? $this->request->get[\'layoutbox\'] : \'\' ; \r\n		 $data[\'url_pattern\'] = isset($this->request->get[\'pattern\']) ? $this->request->get[\'pattern\'] : \'\' ; \r\n		 $data[\'account_fb\'] = isset($this->request->get[\'account_fb\']) ? $this->request->get[\'account_fb\'] : \'\' ; \r\n		 $data[\'compare\'] = $this->url->link(\'product/compare\', \'\', true); \r\n		\r\n		// add position\r\n		$data[\'header_block\'] = $this->load->controller(\'extension/soconfig/header_block\');\r\n		$data[\'content_home1\'] = $this->load->controller(\'extension/soconfig/content_home1\');\r\n		$data[\'content_home2\'] = $this->load->controller(\'extension/soconfig/content_home2\');\r\n		\r\n		// For page specific css\r\n		if (isset($this->request->get[\'route\'])) $data[\'class\'] = str_replace(\'/\', \'-\', $this->request->get[\'route\']);\r\n		else $data[\'class\'] = \'common-home\';\r\n		\r\n		//Decodes HTML Entities\r\n         $data[\'selector_body\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'selector_body\'), ENT_QUOTES, \'UTF-8\');\r\n         $data[\'selector_menu\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'selector_menu\'), ENT_QUOTES, \'UTF-8\');\r\n         $data[\'selector_heading\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'selector_heading\'), ENT_QUOTES, \'UTF-8\');\r\n		$data[\'mselector_body\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'mselector_body\'), ENT_QUOTES, \'UTF-8\');\r\n         $data[\'mselector_menu\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'mselector_menu\'), ENT_QUOTES, \'UTF-8\');\r\n         $data[\'mselector_heading\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'mselector_heading\'), ENT_QUOTES, \'UTF-8\');\r\n		\r\n		 if (!defined (\'OWL_CAROUSEL\')){ \r\n		 $this->document->addStyle(\'catalog/view/javascript/soconfig/css/owl.carousel.css\'); \r\n		 $this->document->addScript(\'catalog/view/javascript/soconfig/js/owl.carousel.js\'); \r\n		 define( \'OWL_CAROUSEL\', 1 ); \r\n		 } \r\n		]]></add>\r\n		</operation>\r\n	</file>\r\n	\r\n	<file path=\"catalog/controller/common/footer.php\">\r\n		<operation >\r\n			<search ><![CDATA[public function index()]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n		/*======Show Themeconfig=======*/\r\n		$data[\'soconfig\'] = $this->soconfig;\r\n		$this->load->language(\'extension/soconfig/compare\');\r\n		$data[\'objlang\'] = $this->language;\r\n		$data[\'lang_id\'] = $this->config->get(\'config_language_id\');\r\n		$data[\'theme_directory\'] = $this->config->get(\'theme_default_directory\');\r\n		$data[\'account_fb\'] = isset($this->request->get[\'account_fb\']) ? $this->request->get[\'account_fb\'] : \'\' ;\r\n		$data[\'compare\'] = $this->url->link(\'product/compare\', \'\', true);\r\n		\r\n		// add position\r\n		 $data[\'footer_block1\'] = $this->load->controller(\'extension/soconfig/footer_block1\');\r\n		 $data[\'footer_block2\'] = $this->load->controller(\'extension/soconfig/footer_block2\');\r\n	     $data[\'footer_block3\'] = $this->load->controller(\'extension/soconfig/footer_block3\');\r\n\r\n		\r\n		\r\n		]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/controller/common/maintenance.php\">\r\n		<operation >\r\n			<search ><![CDATA[public function index()]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n				/*======Show Themeconfig=======*/\r\n				$this->load->language(\'extension/soconfig/soconfig\');\r\n				$data[\'objlang\'] = $this->language;\r\n				$data[\'soconfig\'] = $this->soconfig;\r\n				if ($this->request->server[\'HTTPS\']) $server = $this->config->get(\'config_ssl\');\r\n				else $server = $this->config->get(\'config_url\');\r\n				$data[\'config_logo\'] = $server.\'image/\'.$this->config->get(\'config_logo\');\r\n				$data[\'comingsoon_content\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'comingsoon_content\'), ENT_QUOTES, \'UTF-8\');	\r\n                $data[\'content_comingsoon\'] = $this->load->controller(\'extension/soconfig/content_soon\');\r\n			]]></add>\r\n		</operation>\r\n       \r\n    </file>\r\n    <fil