(19, 0, 'So Product Feature', 'ocmod-productoption', 'OpencartWorks', '1.0.0', 'http://www.OpencartWorks.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?> \r\n<modification> \r\n <name><![CDATA[So Product Feature]]></name> \r\n <code>ocmod-productoption</code> \r\n <version><![CDATA[1.0.0]]></version> \r\n <author><![CDATA[OpencartWorks]]></author> \r\n <link>http://www.OpencartWorks.com</link> \r\n \r\n<!--========Catalog/controller/product=============--> \r\n<file path=\"admin/language/*/catalog/product.php\">\r\n	<operation>\r\n		 <search><![CDATA[// Entry]]></search>\r\n		<add position=\"after\"><![CDATA[\r\n			// Backend Product Options\r\n			$_[\'tab_feature\']                     = \'SO Feature <span class=\"label label-primary\">News</span>\';\r\n			$_[\'entry_video_link\']                = \'Video youtube link\';\r\n			$_[\'entry_custom_tab_title\']          = \'Title custom tab \';\r\n			$_[\'entry_description_custom_tab\']          = \'Description custom tab \';\r\n\r\n		]]></add>\r\n	</operation>\r\n</file>\r\n <file path=\"admin/model/catalog/product.php\">\r\n	<operation>\r\n		<search><![CDATA[$this->db->query(\"INSERT INTO \" . DB_PREFIX . \"product_description SET product_id = \'\" . (int)$product_id . \"\', language_id = \'\" . (int)$language_id . \"\', name = \'\" . $this->db->escape($value[\'name\']) . \"\', description = \'\" . $this->db->escape($value[\'description\']) . \"\', tag = \'\" . $this->db->escape($value[\'tag\']) . \"\', meta_title = \'\" . $this->db->escape($value[\'meta_title\']) . \"\', meta_description = \'\" . $this->db->escape($value[\'meta_description\']) . \"\', meta_keyword = \'\" . $this->db->escape($value[\'meta_keyword\']) . \"\'\");]]></search>\r\n		<add position=\"replace\"><![CDATA[\r\n			if ($this->checkProductOptionColumnExist()) {\r\n				$this->db->query(\"INSERT INTO \" . DB_PREFIX . \"product_description SET product_id = \'\" . (int)$product_id . \"\', language_id = \'\" . (int)$language_id . \"\', name = \'\" . $